mapper.select:
SELECT b.bookmark_id, b.user_id, u.user_nickname, b.question_id, q.question_title FROM tb_bookmark b JOIN tb_user u ON b.user_id = u.user_id JOIN tb_question q ON b.question_id = q.question_id where 1=1
AND b.question_id = #{questionId}
ORDER BY b.bookmark_id DESC
LIMIT #{size} OFFSET #{offset}

SELECT b.bookmark_id, b.user_id, u.user_nickname, b.question_id, q.question_title FROM tb_bookmark b JOIN tb_user u ON b.user_id = u.user_id JOIN tb_question q ON b.question_id = q.question_id WHERE b.user_id = #{userId}
AND b.question_id = #{questionId}
ORDER BY b.bookmark_id DESC
LIMIT #{size} OFFSET #{offset}
