SELECT user_id, user_email, user_password, user_nickname, user_status, user_role, created_at, updated_at FROM tb_user WHERE 1=1
AND user_nickname LIKE CONCAT('%', #{keyword}, '%')
AND user_nickname = #{userNickname}
AND user_status = #{userStatus}
AND user_role = #{userRole}
ORDER BY created_at DESC LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*)
FROM tb_user
WHERE 1=1

    AND user_nickname LIKE CONCAT('%', #{keyword}, '%')


    AND user_nickname = #{userNickname}


    AND user_status = #{userStatus}


    AND user_role = #{userRole}
