mapper.select:
SELECT ug.user_grade_id , u.user_id, u.user_nickname, g.grade_id, g.grade_name, c.category_id, c.category_name, ug.victory_count FROM tb_user_grade ug JOIN tb_user u ON ug.user_id = u.user_id JOIN tb_grade g ON ug.grade_id = g.grade_id JOIN tb_category c ON ug.category_id = c.category_id WHERE 1=1
AND u.user_id = #{userId}
AND c.category_id = #{categoryId}
AND g.grade_id = #{gradeId}
AND u.user_nickname LIKE CONCAT('%', #{keyword}, '%') OR c.category_name LIKE CONCAT('%', #{keyword}, '%')
ORDER BY ug.user_grade_id ASC LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*) FROM tb_user_grade ug JOIN tb_user u JOIN tb_grade g JOIN tb_category c WHERE 1=1
AND u.user_id = #{userId}
AND c.category_id = #{categoryId}
AND g.grade_id = #{gradeId}
AND u.user_nickname LIKE CONCAT('%', #{keyword}, '%') OR c.category_name LIKE CONCAT('%', #{keyword}, '%')
