mapper.select:
SELECT chat_member_id, joined_at, left_at, chat_room_id, user_id, is_read FROM tb_chat_member WHERE 1=1 AND left_at IS NULL
AND message_content LIKE CONCAT('%', #{keyword}, '%')
AND chat_room_id = #{chatRoomId}
AND user_id = #{userId}
ORDER BY joined_at DESC LIMIT #{size} OFFSET #{offset}
